INSERT INTO `cms_post` VALUES ('7', '1761432744067908', '1', '2', 'yii框架使用笔记-获取http请求和设置http响应', 'nbsp,yii,response-,headers-,aaaaa,php,add,框架,request-,quot', 'Yii Framework是一个基于组件、用于开发大型 Web 应用的高性能 PHP 框架。Yii提供了今日Web 2.0应用开发所需要的几乎一切功能', '', 'yii', '66', null, '0', '0', '0', '2017-09-13 23:21:01', '2017-12-29 17:18:20');
