DROP TABLE IF EXISTS `admin_access`;
CREATE TABLE `admin_access` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '权限id',
  `pid` text NOT NULL,
  `access_sn` varchar(50) NOT NULL,
  `access_name` varchar(45) NOT NULL COMMENT '访问表',
  `module` varchar(45) DEFAULT NULL,
  `controller` varchar(45) DEFAULT NULL,
  `action` varchar(45) DEFAULT NULL,
  `level` smallint(3) DEFAULT NULL COMMENT '1:一级菜单 2:二级菜单 3:三级菜单(按钮)',
  `sort` smallint(3) NOT NULL DEFAULT '100' COMMENT '排序值',
  `deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否删除',
  `created` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `modified` timestamp NULL DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1427 DEFAULT CHARSET=utf8 COMMENT='权限访问表';

