-- ----------------------------
-- Table structure for cms_category
-- ----------------------------
DROP TABLE IF EXISTS `cms_category`;
CREATE TABLE `cms_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '父级栏目',
  `model_id` int(5) NOT NULL COMMENT '模型id',
  `category_name` varchar(100) NOT NULL COMMENT '栏目名称',
  `category_alias` varchar(100) NOT NULL COMMENT '栏目别名',
  `category_type` varchar(30) NOT NULL DEFAULT 'list' COMMENT 'list:最终列表栏目,channel:频道封面',
  `keywords` varchar(100) DEFAULT NULL COMMENT '关键字',
  `description` varchar(200) DEFAULT NULL COMMENT '描述',
  `list_template` varchar(50) DEFAULT NULL COMMENT '列表页模版',
  `detail_template` varchar(50) DEFAULT NULL COMMENT '详情页模版',
  `channel_template` varchar(50) DEFAULT NULL COMMENT '频道页模版',
  `content` text COMMENT '栏目内容(类似关于我们这样的页面可以用到)',
  `deleted` int(1) NOT NULL DEFAULT '0' COMMENT '是否删除',
  `created` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `modified` timestamp NULL DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='栏目表';

-- ----------------------------
-- Records of cms_category
-- ----------------------------

-- ----------------------------
-- Table structure for cms_post
-- ----------------------------
DROP TABLE IF EXISTS `cms_post`;
CREATE TABLE `cms_post` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `post_id` varchar(20) NOT NULL COMMENT '文档id',
  `category_id` int(11) DEFAULT NULL COMMENT '所属栏目',
  `model_id` int(11) DEFAULT NULL COMMENT '所属模型',
  `title` varchar(100) DEFAULT NULL COMMENT '文档标题',
  `keywords` varchar(100) DEFAULT NULL COMMENT '文档关键词',
  `description` varchar(200) DEFAULT NULL COMMENT '文档描述',
  `main_image` varchar(50) DEFAULT NULL COMMENT '文档主图',
  `post_tag` varchar(100) DEFAULT NULL COMMENT '文档标签，(,隔开)',
  `click` int(11) NOT NULL DEFAULT '0' COMMENT '点击数',
  `author` varchar(50) DEFAULT NULL COMMENT '作者',
  `is_publish` tinyint(3) NOT NULL DEFAULT '0' COMMENT '是否发布(0未发布,10已发布)',
  `is_recommed` varchar(100) NOT NULL DEFAULT '0' COMMENT '是否推荐(0为推荐,10已推荐)',
  `deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否删除(0未删除，1已删除)',
  `created` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `modified` timestamp NULL DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `post_id_unique_index` (`post_id`) USING BTREE COMMENT 'post_id唯一索引'
) ENGINE=InnoDB AUTO_INCREMENT=48 DEFAULT CHARSET=utf8 COMMENT='文档表';

-- ----------------------------
-- Records of cms_post
-- ----------------------------

